import { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: "Harish",
      company: "Zelar",
    };
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hi {this.state.name}, Iam working at {this.state.company}
          </p>
          <button
            onClick={() => {
              this.setState(
                () => {
                  return { name: "S.Harish" };
                },
                () => {
                  console.log(this.state);
                }
              );
            }}
          >
            {" "}
            change name
          </button>
        </header>
      </div>
    );
  }
}

export default App;
